import { defineStore } from 'pinia';

export const useDataStore = defineStore('dataStore', {
  state: () => ({
    workItem: [],
  }),
  actions: {
    nextWorkItem() {

    },
    prevWorkItem() {
    },
    workItemsObj(items) { 
      this.workItem = items.map((item, index) => ({ ...item, index }));
    }
  }
});
