import { defineStore } from 'pinia';

export const useDataStore = defineStore('dataStore', {
  state: () => ({
    workItem: [] // 추가
  }),
  actions: {
    nextWorkItem() {
        this.workItem.id +=1;
        console.log( "next" );
    },
    prevWorkItem() {
        console.log( "prev" );
    },
    workItemsObj(items) { 
        console.log(items);
        this.workItem = items;
    }
  }
});
