import { createWebHashHistory, createRouter } from "vue-router";
import { gsap, Expo } from 'gsap';

// Timeline instance
const tl = gsap.timeline();

// Function to reset wave and contents
const waveReset = (wave, tl, contents) => {
  tl.to(contents, 0, { opacity: 1 });
  wave.style.opacity = 0;
  tl.to(wave, 0.75, { y: "100%", ease: Expo.easeInOut });
};

// Function to show wave and hide contents
const waveShow = (wave, tl, contents, next, ) => {
  // main.style.opacity = 0;
  tl.to(contents, 0, { opacity: 0 });
  wave.style.opacity = 1;
  tl.to(wave, 0.75, { y: 0, ease: Expo.easeInOut });
  tl.to(wave, 0.75, {
    y: "-110%",
    ease: Expo.easeInOut,
    onComplete: () => {
      waveReset(wave, tl, contents);
      next();
    },
  });
};

// Define route configurations
const routes = [
  {
    path: "/",
    name: "Main",
    component: () => import("@/views/Main.vue"),
  },
  {
    path: "/Work",
    name: "Work",
    component: () => import("@/views/Work/Index.vue"),
  },
  {
    path: "/WorkView:id",
    name: "WorkView",
    component: () => import("@/views/Work/View.vue"),
    meta: { requiresAuth: true },
  },
  {
    path: "/Contact",
    name: "Contact",
    component: () => import("@/views/Contact/Index.vue"),
  },
  {
    path: "/Blog",
    name: "Blog",
    component: () => import("@/views/Blog/Index.vue"),
  },
  {
    path: "/BlogView:id",
    name: "BlogView",
    component: () => import("@/views/Blog/View.vue"),
    meta: { requiresAuth: true },
  },
];

// Create router instance
const router = createRouter({
  history: createWebHashHistory(),
  routes,
  scrollBehavior(to, from, savedPosition) {
    if (savedPosition) {
      return savedPosition;
    } else {
      return { top: 0 };
    }
  },
});

// Navigation guard
document.addEventListener('DOMContentLoaded', () => {
  router.beforeEach((to, from, next) => {
    const header = document.getElementsByTagName("header")[0];
    const wave = document.getElementById("wave");
    const contents = gsap.utils.toArray("header, nav, main, footer");
    const main = document.querySelector('main');
  
    console.log( main ); 
      // Now you can perform actions with the main element
      if (main) {
        // For example, add a CSS class to the main element
        main.classList.add('some-class');
      }
  
  
    if (!header.classList.contains('open')) {
      // Show wave and hide contents before navigating
      waveShow(wave, tl, contents, next );
      // setTimeout(() => {
      //   next();
      // }, 1000);
    }
  })
});

export default router;
