import { defineStore } from 'pinia';
import { computed, ref } from "vue";


export const useWorkStore = defineStore('workStore', {
  state: () => ({
    currentPage: 1,
    itemsPerPage: 5, 
    itemLength: 100
  }),
  getters: {
    startIndex: (state) => (state.currentPage - 1) * state.itemsPerPage,
    endIndex: (state, getters) => getters.startIndex + state.itemsPerPage - 1,
 
  },
  actions: {
    setCurrentPage(page) {
      this.currentPage = page;
    },
  },
});
