// src/store/index.js
import { createStore } from 'vuex';

export default createStore({
  state: {
    prevProjectInfo: {
    },
    nextProjectInfo: {
    },
  },
  mutations: {
    setPrevPageData(state, data) {
        console.log(  state  )
        state.prevProjectInfo = data;
    },
    setNextPageData(state, data) {
        console.log(  state  )
        state.nextProjectInfo = data;
    },

  },
});
