import { createWebHistory, createRouter } from "vue-router";
import { gsap, Expo } from 'gsap';

const tl = gsap.timeline();

const waveReset = (wave, tl) => {
  wave.style.opacity = 0;
  tl.to(wave, 0.75, { y: "100%", ease: Expo.easeInOut });
};

const waveShow = (wave, tl, next) => {
  wave.style.opacity = 1;
  tl.to(wave, 0.75, { y: 0, ease: Expo.easeInOut });
  tl.to(wave, 0.75, {
    y: "-110%",
    ease: Expo.easeInOut,
    onComplete: () => {
      next();
      waveReset(wave, tl);
    },
  });
};

const routes = [
  {
    path: "/",
    name: "Main",
    component: () => import("@/views/Main.vue"),
    beforeEnter: (to, from, next) => {
      const header = document.getElementsByTagName("header")[0];
      const wave = document.getElementById("wave");
      if (!header.classList.contains('open')) {
        waveShow(wave, tl, next);
      }
    }
  },
  {
    path: "/about",
    name: "About",
    component: () => import("@/views/About.vue"),
    beforeEnter: (to, from, next) => {
      const header = document.getElementsByTagName("header")[0];
      const wave = document.getElementById("wave");
      if (!header.classList.contains('open')) {
        waveShow(wave, tl, next);
      }
    }
  },
  {
    path: "/Work",
    name: "Work",
    component: () => import("@/views/Work.vue"),
    beforeEnter: (to, from, next) => {
      const header = document.getElementsByTagName("header")[0];
      const wave = document.getElementById("wave");
      if (!header.classList.contains('open')) {
        waveShow(wave, tl, next);
      }else{
        console.log( "여기일때?" );
      }
    }
  },
  {
    path: "/WorkView",
    name: "WorkView",
    component: () => import("@/components/Layouts/Work/WorkView.vue"),
    beforeEnter: (to, from, next) => {
      const header = document.getElementsByTagName("header")[0];
      const wave = document.getElementById("wave");
      if (!header.classList.contains('open')) {
        waveShow(wave, tl, next);
      }
    }
  },
  {
    path: "/Contact",
    name: "Contact",
    component: () => import("@/views/Contact.vue"),
    beforeEnter: (to, from, next) => {
      const header = document.getElementsByTagName("header")[0];
      const wave = document.getElementById("wave");
      if (!header.classList.contains('open')) {
        waveShow(wave, tl, next);
      }
    }
  }
];

const router = createRouter({
  history: createWebHistory(),
  routes,
  scrollBehavior: function(to, from, savedPosition) {
    let position = {}
    if (to.hash) {
      position = {
        selector : to.hash
      };
    } else {
      position = {x : 0 , y : 0}
    }
    return position;
  }
});

export default router;
