const globalResizeMixin = {
  mounted() {
    console.log( "??" );
    window.addEventListener('resize', this.handleResize);
  },
  beforeUnmount() {
    window.removeEventListener('resize', this.handleResize);
  },
  methods: {
    handleResize() {

      // Perform your desired actions on resize event
      console.log('Window resized');
    },
  },
};

export default globalResizeMixin
