import { createWebHistory, createRouter } from "vue-router";
import Main from "@/views/Main.vue";
import About from "@/views/About.vue";
import Work from "@/views/Work.vue";
import WorkView from "@/components/Layouts/Work/WorkView.vue";
import Contact from "@/views/Contact.vue";
import { gsap, Expo } from 'gsap';

const routes = [
  // {
  //   // path: "/",
  //   // redirect: "/"
  // },
  {
    path: "/",
    name: "Main",
    component: Main,
  },
  {
    path: "/about",
    name: "About",
    component: About,
    beforeEnter: (to, from, next) => {
      // console.log( to, from, next ); 
      const wave = document.getElementById( "wave" );
      const tl = gsap.timeline();
      const header = document.getElementsByTagName( "header" );
      const nav = document.querySelector( ".nav" );
      console.log(  header[0].classList);

      const waveReset = () => {
        wave.style.opacity = 0;
        tl.to( wave, 0.75, { y:"100%", ease:Expo.easeInOut});
      }
      
      
      const waveShow = () => {
        wave.style.opacity = 1;
        tl.to( wave, 0.75, { y:0, ease:Expo.easeInOut});
        tl.to( wave, 0.75, { y: -110+"%", ease:Expo.easeInOut, onComplete: () =>{
          next();
          waveReset();
        }});
      }


      if( header[0].classList.contains('open') ){
    
      }else{

       
        waveShow();
      }
    }
  },

  {
    path: "/Work",
    name: "Work",
    component: Work,
  },

  {
    path: "/WorkView",
    name: "WorkView",
    component: WorkView,
  },

  {
    path: "/Contact",
    name: "Contact",
    component: Contact,
  },

];

const router = createRouter({
  history: createWebHistory(),
  routes,
});




router.beforeEach((to, from, next) => {
    // const wave = document.getElementById( "wave" );
    // const tl = gsap.timeline;
    // // tl.gsap.
    // gsap.set( wave, { y:100+"%" })
    // gsap.to( wave, 0.75, { y:0, ease:Expo.easeInOut, onComplete: ()=> { next();  }})
    // console.log( wave );
    // console.log( to, from, next );
    // Call next() to proceed with the route navigation
    next();
});


export default router;
