export default function (app) {
  app.mixin({

    mounted() {
      window.addEventListener('load', this.loadWidthCheck);
      window.addEventListener('resize', this.handleResize);
      this.handleResize();
    },
    beforeUnmount() {
      window.removeEventListener('load', this.loadWidthCheck);
      window.removeEventListener('resize', this.handleResize);
    },
    methods: {

      loadWidthCheck(){
        const deviceWidth = window.matchMedia("screen and (max-width: 768px)");
        deviceWidth.addListener(function(e) {
          if(e.matches) {
            console.log('모바일 화면 입니다.');
          } else {
            console.log('데스크탑 화면 입니다.');
          }
        });
      },
      handleResize() {
        // const deviceWidth = window.matchMedia("screen and (max-width: 768px)");
        // let vh = window.innerHeight;
        // let vw = window.innerWidth;
        //세로
        const vh = window.innerHeight * 0.01;   // [1]
        //가로
        const vw = window.innerWidth * 0.01;   // [1]
        // document.documentElement.style.setProperty('--vw', `${vw}px`); // [2]
      },
    }
  });
}
