// src/store/index.js
import { createStore } from 'vuex';

export default createStore({
  state: {
    prevProjectInfo: {
        item:0
    },
    nextProjectInfo: {
        item:0
    },
  },
  mutations: {
    setPrevPageData(state, data) {
        console.log( "prev data===>", data );
        state.prevProjectInfo = data;
    },
    setNextPageData(state, data) {
        // console.log( "next data===>", data );
        state.item++;
        console.log( "state", state.prevProjectInfo.item );
        state.nextProjectInfo = data;
    },

  },
});
