import { defineStore } from 'pinia'

export const useCounterStore = defineStore('counter', {

  state: () => ({
    count: 0,
    workitems: [] // 추가
  }),
  actions: {
    increment() {
        this.count++;
        this.workitems = this.workitems[this.count++];
        console.log( this.count );
    },
    decrement() {
        this.count--;
        this.workitems = this.workitems[this.count--];
        console.log( this.count );
    },
    setWorkItems(workitems) { // 추가
        this.workitems = workitems;
    }
  }
})
