// src/store/index.js
import { createStore } from 'vuex';

export default createStore({
  state: {
    // 상태(데이터)를 정의합니다.
    prevProjectInfo: {},
    nextProjectInfo: {},
    // ... 추가적인 상태들 ...
  },
  mutations: {
    // 상태를 변경하는 동작(뮤테이션)을 정의합니다.
    setPrevPageData(state, data) {
        console.log( state );
        state.prevProjectInfo = data;
    },
    setNextPageData(state, data) {
        console.log( state );
        state.nextProjectInfo = data;
    },
    // ... 추가적인 뮤테이션들 ...
  },
  // getters, actions 등도 추가할 수 있습니다.
});
