import { defineStore } from 'pinia';

export const useDataStore = defineStore('dataStore', {
  state: () => ({
    workItem: [],
    currentIndex: 0,
  }),
  actions: {
    nextWorkItem() {
      if (this.currentIndex < this.workItem.length - 1) {
        this.currentIndex += 1;
        console.log("Next index:", this.currentIndex);
      } else {
        console.log("No more items.");
      }
    },
    prevWorkItem() {
        console.log( "prev" );
    },
    workItemsObj(items) { 
      this.workItem = items.map((item, index) => ({ ...item, index }));
      console.log( this.workItem );
    }
  }
});
